		public TestReport(double value)
		{ score = value; }

		//Prints grade and comment.
		public void printGrade()
		{
			if (score >= 90.0)
				System.out.println("A. Congratulations!");
			if (score >= 80.0)
				System.out.println("B. Good job.");
