import java.util.*;

public class APLine {
    private int a;
    private int b;
    private int c;

    public APLine(int x, int y, int z)
    {
        if (x == 0 || y == 0)
        {
            throw new IllegalArgumentException("a and b must be nonzero integers");
        }

        a = x;
        b = y;
        c = z;
    }

    public double getSlope()
    { 
        double slope = - (double)a / (double)b;
        return slope;
    }

    public boolean isOnLine(int i, int j)
    {
        return ((a * i) + (b * j) + c == 0);
    }
}

