public static int[] triangular (int n)
{
    int[] a = new int[n];
    a[0] = 1;
    int count = 1;
    int triangular = 2;
    while (n > count)
    {
        a[count] = triangular + a[count - 1];
        count++;	
        triangular++;
    }
    return a;
}

