public static boolean isSelfDivisor(int number)
{
    int k = 0;
    int n = number;
    while (n > 0)
    {
        k = n % 10;
        if (k == 0 || number % k != 0)
            return false;
        n /= 10;
    }
    return true;
}

(b)
public static int[] firstNumSelfDivisors(int start, int num)
{
    int[] a = new int[num];
    int j = start;
    int k = 0;
    while (k < num)
    {
        if (isSelfDivisor(j))
            a[k++] = j;
        j++;
    }
    return a;
}

