a)  private void fillBlock(String str)
    {
        int i = 0; 
        for (int r = 0; r < numRows; r++)
        {
            for (int c = o; c < numCols; c++) 
            { 
                if (i < str.length())
                   letterBlock[r][c]= str.substring(i,i+1); 
                else
                   letterBlock[r][c] = "A";
                    i++;
            }
        }
    }

b)  private String encryptBlock()
    {
        String s = ""; 
        for (int c = 0; c < numCols; c++)
        {
            for (int r = 0; r < numRows, r++)
            {
                s += letterBlock[r][c]; 
            }
        }
        return s;
    }

c)  public String encrypt Message (String message)
    {
        String c = ""; 
        while (message.length() > 0)
        {
            int n = numRows * numcols; 
            if (n > message.length())
                n = message.length();
            fillBlock(message.substring(0,n)); 
            c += encryptBlock(); 
            message = message.substring(n);
        }
        return c;
    }

